import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { JogadorService } from '../jogador.service';

@Component({
  selector: 'app-listar-jogadores',
  templateUrl: './listar-jogadores.component.html',
  styleUrls: ['./listar-jogadores.component.css']
})
export class ListarJogadoresComponent implements OnInit {

  jogadorSelecionado: any;

  jogadores: any[] = []

  constructor(private router: Router, private jogadorService: JogadorService) { }

  ngOnInit(): void {
    this.carregarJogadores();
  }

  carregarJogadores(){
    this.jogadorService.todosJogadores();
  }

  selecionarJogador(jogador: any) {
    this.jogadorSelecionado = jogador;
    this.router.navigate(['/editar-jogador/' + this.jogadorSelecionado.id], { state: { jogador: jogador } });
  }

  excluirJogador(jogador: any){
    this.jogadorSelecionado = jogador;
    if (this.jogadorSelecionado) {
      const indice = this.jogadores.findIndex(clube => clube.id === this.jogadorSelecionado.id);
      if (indice !== -1) {
        this.jogadores.splice(indice, 1);
        localStorage.setItem('jogadores', JSON.stringify(this.jogadores));
        this.jogadorSelecionado = null;
        this.router.navigate(['/home']);
      }
    }
  }

}
<div class="card-container">
  <div class="card" *ngFor="let jogador of jogadores">
    <div class="card-content">
      <h3>{{ jogador.nome }}</h3>
      <p>Posição: {{ jogador.posicao }}</p>
      <p>Altura: {{ jogador.altura }}</p>
      <p>Data de Nascimento: {{ jogador.dataNascimento }}</p>
      <p>Clube: {{ jogador.clube }}</p>
    </div>
    <div class="card-actions">
      <button [routerLink]="'/editar-jogador/' + jogador.nome" (click)="selecionarJogador(jogador)" class="btn">Editar</button>
      <button (click) = "this.excluirJogador(jogador)" class="btn">Excluir</button>
    </div>
  </div>
</div>
