import { Jogador } from './../model/jogador';
import { Component, OnInit, ViewChild } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Shared } from '../util/shared';
import { UserStorageService } from './jogador-storage.service';

@Component({
  selector: 'app-cadastro-jogador',
  templateUrl: './cadastro-jogador.component.html',
  styleUrls: ['./cadastro-jogador.component.css']
})
export class CadastroJogadorComponent {
  @ViewChild('form') form!: NgForm;

  jogador!: Jogador;
  jogadores?: Jogador[];

  constructor() {
    this.loadFormData(); // Carregar dados do jogador do localStorage, se existirem
  }

  submitForm(form: NgForm) {
    if (form.valid) {
      this.saveFormData();
    }
  }

  saveFormData() {
    localStorage.setItem('jogador', JSON.stringify(this.jogador));
  }

  loadFormData() {
    const jogadorData = localStorage.getItem('jogador');
    if (jogadorData) {
      this.jogador = JSON.parse(jogadorData);
      console.log(this.jogador.nome)
    }
  }

}
<form #form="ngForm" (ngSubmit)="submitForm(form)">
  <div>
    <label for="nome">Nome:</label>
    <input type="text" id="nome" name="nome" class="validate" [(ngModel)]="jogador.nome" pattern="^(\s?[A-ZÀ-Ú][a-zà-ú]*)+(\s[a-zà-ú]*)?(\s[A-ZÀ-Ú][a-zà-ú]*)+" #nome="ngModel" required>
    <div class="red-text" [hidden]="nome.valid || nome.untouched">
      Por favor, informe o seu nome completo.
    </div>
    <div class="red-text" *ngIf="nome.errors?.['required'] && nome.touched">
      Por favor, o nome completo é obrigatório.
    </div>
    <span class="helper-text" data-error="O campo é inválido" data-success="Ok!"></span>
  </div>
  <div>
    <label for="posicao">Posição:</label>
    <input type="text" id="posicao" name="posicao" class="validate" [(ngModel)]="jogador.posicao" pattern="^(\s?[A-Za-zÀ-ú][a-zà-ú]*)+(\s[A-Za-zÀ-ú][a-zà-ú]*)?" #posicao="ngModel" required>
    <div class="red-text" [hidden]="posicao.valid || posicao.untouched">
      Por favor, informe a posição do jogador.
    </div>
    <div class="red-text" *ngIf="posicao.errors?.['required'] && posicao.touched">
      A posição do jogador é obrigatória.
    </div>
    <span class="helper-text" data-error="O campo é inválido" data-success="Ok!"></span>
  </div>
  <div>
    <label for="altura">Altura:</label>
    <input type="text" id="altura" name="altura" class="validate" [(ngModel)]="jogador.altura" pattern="\d+(\.\d{1,2})?" #altura="ngModel" required>
    <div class="red-text" [hidden]="altura.valid || altura.untouched">
      Por favor, informe a altura do jogador (no formato x.xx).
    </div>
    <div class="red-text" *ngIf="altura.errors?.['required'] && altura.touched">
      A altura do jogador é obrigatória.
    </div>
    <span class="helper-text" data-error="O campo é inválido" data-success="Ok!"></span>
  </div>
  <div>
    <label for="dataNascimento">Data de Nascimento:</label>
    <input type="text" id="dataNascimento" name="dataNascimento" class="validate" [(ngModel)]="jogador.dataNascimento" pattern="\d{2}\/\d{2}\/\d{4}" #dataNascimento="ngModel" required>
    <div class="red-text" [hidden]="dataNascimento.valid || dataNascimento.untouched">
      Por favor, informe a data de nascimento do jogador (no formato dd/mm/aaaa).
    </div>
    <div class="red-text" *ngIf="dataNascimento.errors?.['required'] && dataNascimento.touched">
      A data de nascimento do jogador é obrigatória.
    </div>
    <span class="helper-text" data-error="O campo é inválido" data-success="Ok!"></span>
  </div>
  <div>
    <label for="clube">Clube:</label>
    <input type="text" id="clube" name="clube" class="validate" [(ngModel)]="jogador.clube" #clube="ngModel" required>
    <div class="red-text" [hidden]="clube.valid || clube.untouched">
      Por favor, informe o clube do jogador.
    </div>
    <div class="red-text" *ngIf="clube.errors?.['required'] && clube.touched">
      O clube do jogador é obrigatório.
    </div>
    <span class="helper-text" data-error="O campo é inválido" data-success="Ok!"></span>
  </div>
  <div class="buttons">
    <button type="submit" [disabled]="!form.valid">OK</button>
    <button type="button">Cancelar</button>
  </div>
</form>
