import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-editar-jogador',
  templateUrl: './editar-jogador.component.html',
  styleUrls: ['./editar-jogador.component.css']
})
export class EditarJogadorComponent implements OnInit{

  jogadorId: string | undefined;
  jogador: any;


  constructor(private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.jogadorId = params.get('id') ?? undefined;
      this.jogador = this.getJogadorById(this.jogadorId);
    });
  }

  getJogadorById(id: string | undefined) {
    // Implemente a lógica para buscar o jogador pelo ID no array de jogadores
    // Neste exemplo, estou retornando o jogador com o nome "Jogador 1"
    return {
      nome: 'Jogador 1',
      posicao: 'Atacante',
      altura: '1.80m',
      dataNascimento: '01/01/1990',
      clube: 'Clube A'
    };
  }

}
