import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-editar-jogador',
  templateUrl: './editar-jogador.component.html',
  styleUrls: ['./editar-jogador.component.css']
})
export class EditarJogadorComponent implements OnInit{

  @Input() jogador2: any;
  jogadorNome: string | undefined;
  jogador: any;

  constructor(private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.jogadorNome = params.get('nome') ?? undefined;
      this.jogador = this.getJogadorByNome(this.jogadorNome);
    });
  }

  getJogadorByNome(nome: string | undefined) {
    return {
      nome: 'Jogador 1',
      posicao: 'Atacante',
      altura: '1.80m',
      dataNascimento: '01/01/1990',
      clube: 'Clube A'
    };
  }

}
