import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-listar-jogadores',
  templateUrl: './listar-jogadores.component.html',
  styleUrls: ['./listar-jogadores.component.css']
})
export class ListarJogadoresComponent implements OnInit {

  parametro: string | undefined;

  constructor(private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      this.parametro = params.get('parametro') ?? undefined;
    });
  }


  jogadores: any[] = [
    {
      nome: 'Jogador 1',
      posicao: 'Atacante',
      altura: '1.80m',
      dataNascimento: '01/01/1990',
      clube: 'Clube A'
    },
    {
      nome: 'Jogador 2',
      posicao: 'Meio-campista',
      altura: '1.75m',
      dataNascimento: '02/02/1995',
      clube: 'Clube B'
    },
  ];

  editarJogador(jogador: any) {
    // Lógica para editar o jogador
  }

  excluirJogador(jogador: any) {
    // Lógica para excluir o jogador
  }
}
<div class="card-container">
  <div class="card" *ngFor="jogadores">
    <div class="card-content">
      <h3>{{ jogador.nome }}</h3>
      <p>Posição: {{ jogador.posicao }}</p>
      <p>Altura: {{ jogador.altura }}</p>
      <p>Data de Nascimento: {{ jogador.dataNascimento }}</p>
      <p>Clube: {{ jogador.clube }}</p>
      <p>Parâmetro recebido: {{ parametro }}</p>
    </div>
    <div class="card-actions">
      <button class="btn" (click)="editarJogador(jogador)">Editar</button>
      <button class="btn" (click)="excluirJogador(jogador)">Excluir</button>
    </div>
  </div>
</div>
