import { Component, Input, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-editar-jogador',
  templateUrl: './editar-jogador.component.html',
  styleUrls: ['./editar-jogador.component.css']
})
export class EditarJogadorComponent implements OnInit {

  @Input() jogador: any;

  constructor(private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.route.paramMap.subscribe(params => {
      const nomeJogador = params.get('nome');
      this.jogador = this.jogador.find((jogador: { nome: string | null; }) => jogador.nome === nomeJogador);
    });
  }
}
<div class="editar-jogador">
  <h2>Editar Jogador</h2>
  <div class="form-group">
    <label>Nome:</label>
    <input [(ngModel)]="jogador.nome" type="text">
  </div>
  <div class="form-group">
    <label>Posição:</label>
    <input [(ngModel)]="jogador.posicao" type="text">
  </div>
  <div class="form-group">
    <label>Altura:</label>
    <input [(ngModel)]="jogador.altura" type="text">
  </div>
  <div class="form-group">
    <label>Data de Nascimento:</label>
    <input [(ngModel)]="jogador.dataNascimento" type="text">
  </div>
  <div class="form-group">
    <label>Clube:</label>
    <input [(ngModel)]="jogador.clube" type="text">
  </div>
  <div class="buttons">
    <button type="submit">OK</button>
    <button type="button">Cancelar</button>
  </div>
</div>
